package com.sameer.firstdayintern

import android.content.Context
import android.content.Intent
import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.sameer.firstdayintern.adapter.RecyclerAdapter
import com.sameer.firstdayintern.model.Items

class FirstActivity : AppCompatActivity() {
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_first)

        val recyclerView : RecyclerView = findViewById(R.id.id_recycler_view2)

        recyclerView.layoutManager = LinearLayoutManager(this)

        val data = ArrayList<Items>()

        for (i in 1..20) {
            data.add(
                Items(1,
                "data:image/jpeg;base64,",
                "Roadside Assistant Service ",
                2000)
            )
        }

        val adapter : RecyclerAdapter = RecyclerAdapter(applicationContext,data)
        recyclerView.adapter = adapter
    }

    companion object{
        fun newIntent(context: Context): Intent {
            return Intent(context, FirstActivity::class.java)
        }
    }
}